table_count_choices = {}

def count_choices(x, y, z, digits):
	if x < 0 or y < 0 or z < 0:
		return 0
	if digits == 0:
		return 1
	count = 0
	upper_i = min(x,digits)
	for i in xrange(upper_i + 1):
		upper_j = min(y, digits - i)
		for j in xrange(upper_j + 1):
			k = digits - i - j
			if k <= z:
				temp = (fact_modular[digits] * fact_modular_inverse[i]) % mod_value
				#print i,j, k, digits, temp
				temp = (temp * fact_modular_inverse[j]) % mod_value
				#print i,j, k, digits, temp
				temp = (temp * fact_modular_inverse[k]) % mod_value
				#print i,j, k, digits, temp
				count = (count + temp) % mod_value
				#print i,j, k, digits, "count = ", count
	#print x, y, z, digits, "done "
	return count

def sum_lucky(x, y, z):
	max_digits = x + y + z
	sum_value = 0
	for num_digits in xrange(1,max_digits+1):
		sum_value = (sum_value + 4*table_of_10s[num_digits]*count_choices(x-1,y,z,num_digits-1)) % mod_value
		sum_value = (sum_value + 5*table_of_10s[num_digits]*count_choices(x,y-1,z,num_digits-1)) % mod_value
		sum_value = (sum_value + 6*table_of_10s[num_digits]*count_choices(x,y,z-1,num_digits-1)) % mod_value
		#print num_digits, sum_value
	return sum_value

